mapper.insert:
insert into item (iid, pid, pTypeid, 
  pStatus, comid, uid, 
  starttime, endtime, remark
  )
values (#{iid,jdbcType=INTEGER}, #{pid,jdbcType=INTEGER}, #{ptypeid,jdbcType=INTEGER}, 
  #{pstatus,jdbcType=INTEGER}, #{comid,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, 
  #{starttime,jdbcType=TIMESTAMP}, #{endtime,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
select

from item
where 1=1

  AND pid = #{pid,jdbcType=INTEGER}


  AND pTypeid = #{ptypeid,jdbcType=INTEGER}


  AND comid = #{comid,jdbcType=INTEGER}


  AND uid = #{uid,jdbcType=INTEGER}
